import React,{useEffect} from 'react';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import About from './components/About';
import Contact from './components/Contact';
import Footer from './components/Footer';

function App() {
  useEffect(() => {
    // Function to handle the parallax effect
    const handleParallax = () => {
      const about = document.querySelector('.about1'); // Select the About section

      const scrollY = window.scrollY; // Get the current scroll position

      // Update the About section's background position to create the parallax effect
      if (about) {
        about.style.backgroundPositionY = `${scrollY * 0.2}px`; // About moves slower (0.2 speed)
      }
    };

    // Add scroll event listener
    window.addEventListener('scroll', handleParallax);

    // Cleanup the event listener on component unmount
    return () => {
      window.removeEventListener('scroll', handleParallax);
    };
  }, []);

  return (
    <>
      <Navbar />
      <Hero />
      <About />
      <Contact />
      <Footer />
    </>

  );
}

export default App;
