import React, { useState } from 'react';
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/style.css';

function Contact() {
  const [phone, setPhone] = useState('');

  const handlePhoneChange = (e) => {
    let input = e.target.value.replace(/\D/g, '');
    if (input.length > 9) input = input.slice(0, 9);

    let formatted = '';
    if (input.length <= 2) formatted = input;
    else if (input.length <= 5) formatted = `${input.slice(0, 2)} ${input.slice(2)}`;
    else formatted = `${input.slice(0, 2)} ${input.slice(2, 5)} ${input.slice(5)}`;

    setPhone(formatted);
  };

  return (
    <section
      id="contact"
      className="contact"
      style={{
        backgroundImage: 'url(/assets/geometric-shape-doodle-seamless-pattern.png)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        display: 'flex',
        height: '100vh',
      }}
    >

      <div className='row'>
        <div className='row-section-1'>
          <img src="/assets/famous passport picture.jpg" alt="Ellipse and Phone" />
          <h1>ABA FAMOUS MAWULENU KWAKU</h1>          
          <p>Designer | Photographer | Computer Engineer</p>
          <p>
            EMAIL<br />
            <a href="mailto:fmkaba12@gmail.com" className="contact-link">
            {/* <img src="assets/542689.png" alt="email icon" className="icon-img" /> */}
            fmkaba12@gmail.com
            </a>
          </p>
          <p>
            PHONE NUMBER<br />
            <a href="https://wa.me/233597972785"
            target="_blank"
            rel="noopener noreferrer"
            className="contact-link">
            +233 59 797 2785
            </a>
          </p>
          <p>
            LOCATION<br />
            Kumasi, Ghana.
          </p>

        </div>
        </div>






      <div className="intro-image-container">
        <h2>Send Us a Message Today</h2>
        {/* <div className="intro-image">
          <img src="https://bookinn-sc77.onrender.com/assets/House.png" alt="House and Magnifier" />
        </div> */}
      </div>



      <div className="contact-form">
        <h2>Send Us a Message Today</h2>

        <div className="form-row">
          <input type="text" placeholder="Name" className="input name" />
          <div className="phone-input">
            <span className="flag">ðŸ‡¬ðŸ‡­ +233</span>
            <input 
              type="text" 
              placeholder="XX XXX XXXX" 
              className="input phone"
              value={phone}
              onChange={handlePhoneChange}
            />
          </div>
        </div>

        <input type="email" placeholder="Email" className="input full-width" />

        <textarea placeholder="Message here" className="input full-width"></textarea>

        <button className="send-btn">Send</button>
      </div>


    </section>
  );
}

export default Contact;
