import React, { useState } from "react";

function Contact() {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  // Format Ghana phone number as before
  const handlePhoneChange = (e) => {
    let input = e.target.value.replace(/\D/g, "");
    if (input.length > 9) input = input.slice(0, 9);

    let formatted = "";
    if (input.length <= 2) formatted = input;
    else if (input.length <= 5) formatted = `${input.slice(0, 2)} ${input.slice(2)}`;
    else formatted = `${input.slice(0, 2)} ${input.slice(2, 5)} ${input.slice(5)}`;

    setPhone(formatted);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!name || !email || !phone || !message) {
      alert("Please fill in all fields!");
      return;
    }

    // Send to Email using mailto
    const mailtoLink = `mailto:fmkaba12@gmail.com?subject=Message from ${name}&body=${message}%0A%0AContact Info:%0AEmail: ${email}%0APhone: ${phone}`;
    window.open(mailtoLink);

    // Send to WhatsApp
    const whatsappNumber = "+233597972785"; // your number
    const whatsappMessage = `Hello! My name is ${name}. ${message} Contact info: ${email}, ${phone}`;
    const whatsappLink = `https://wa.me/${whatsappNumber}?text=${encodeURIComponent(whatsappMessage)}`;
    window.open(whatsappLink, "_blank");

    // Clear the form
    setName("");
    setEmail("");
    setPhone("");
    setMessage("");
  };

  return (
    <section id="contact" className="contact">
      <form className="contact-form" onSubmit={handleSubmit}>
        <h2>Send Me a Message</h2>

        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="input full-width"
        />

        <div className="phone-input">
          <span className="flag">ðŸ‡¬ðŸ‡­ +233</span>
          <input
            type="text"
            placeholder="XX XXX XXXX"
            value={phone}
            onChange={handlePhoneChange}
            className="input phone"
          />
        </div>

        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="input full-width"
        />

        <textarea
          placeholder="Message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="input full-width"
        ></textarea>

        <button type="submit" className="send-btn">
          Send
        </button>
      </form>
    </section>
  );
}

export default Contact;
