import React, { useState } from 'react';

function Contact() {
  const [phone, setPhone] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handlePhoneChange = (e) => {
    let input = e.target.value.replace(/\D/g, '');
    if (input.length > 9) input = input.slice(0, 9);

    let formatted = '';
    if (input.length <= 2) formatted = input;
    else if (input.length <= 5) formatted = `${input.slice(0, 2)} ${input.slice(2)}`;
    else formatted = `${input.slice(0, 2)} ${input.slice(2, 5)} ${input.slice(5)}`;

    setPhone(formatted);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!name || !email || !phone || !message) {
      alert("Please fill all fields");
      return;
    }

    console.log({ name, email, phone, message });
    alert("Form submitted successfully âœ…");
  };

  return (
    <section id="contact" className="contact">

      <form className="contact-form" onSubmit={handleSubmit}>
        <h2>Send Me a Message Today</h2>

        <div className="form-row">
          <input
            type="text"
            placeholder="Name"
            className="input name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />

          <div className="phone-input">
            <span className="flag">ðŸ‡¬ðŸ‡­ +233</span>
            <input
              type="text"
              placeholder="XX XXX XXXX"
              className="input phone"
              value={phone}
              onChange={handlePhoneChange}
            />
          </div>
        </div>

        <input
          type="email"
          placeholder="Email"
          className="input full-width"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <textarea
          placeholder="Message here"
          className="input full-width"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        ></textarea>

        <button className="send-btn" type="submit">Send</button>
      </form>

    </section>
  );
}

export default Contact;
