import React from 'react';

function Hero() {
  const images = [
    "assets/about us.jpg",
    "assets/bellHugs.jpg",
    "assets/david and golaith.jpg",
    "assets/engineer.jpg",
    "assets/jersey vogue.jpg",
    "assets/boutique.jpg",
  ];

  return (
    <section
      id="hero"
      className="hero"
      style={{
        backgroundImage: "url('../assets/Rectangle 3.png')",
        position: 'relative',
        display: 'flex',
        height: '100vh',
        backgroundAttachment: 'scroll',
        zIndex: 1,
      }}
    >
      <img
        src="assets/geometric-shape-doodle-seamless-pattern.png"
        alt="background shape"
        className="background-shape"
      />

      <div className="hero-content">
        <h1>
          Welcome to My <span className="highlight">Portfolio</span>
        </h1>
        <p>
          Here you’ll find a selection of my work. Explore my projects to learn more about what I do.
        </p>
        <button className="exp-btn">
          Explore
          <img
            src={`${process.env.PUBLIC_URL}/assets/arrow.png`}
            alt="arrow"
            style={{ marginLeft: '8px', width: '25px', height: '25px' }}
          />
        </button>
      </div>

      {/* ✅ moving images section */}
      <div className="hero-images">
        {images.map((img, index) => (
          <img key={index} src={img} className="images" alt="slide" />
        ))}

        {/* duplicate for infinite scroll */}
        {images.map((img, index) => (
          <img key={`dup-${index}`} src={img} className="images" alt="slide duplicate" />
        ))}
      </div>
    </section>
  );
}

export default Hero;
