import React, { useState } from 'react';
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/style.css';

function Contact() {
  const [phone, setPhone] = useState('');

  return (
    <section
      id="contact"
      className="contact"
      style={{
        backgroundImage: 'url(/assets/geometric-shape-doodle-seamless-pattern.png)',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        display: 'flex',
        height: '100vh',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <div className="contact-form">
        <h2>Send Me a Message Today</h2>

        <div className="form-row">
          <input type="text" placeholder="Name" className="input name" />

          {/* International Phone Input */}
          <PhoneInput
            country={'gh'}          // default country
            value={phone}
            onChange={setPhone}     // updates state
            placeholder="Phone number"
            inputClass="input phone" 
            inputStyle={{ width: '100%' }}
            enableSearch={true}      // allows searching countries
            specialLabel=""
          />
        </div>

        <input type="email" placeholder="Email" className="input full-width" />
        <textarea placeholder="Message here" className="input full-width"></textarea>

        <button className="send-btn">Send</button>
      </div>
    </section>
  );
}

export default Contact;
